mapper.select:
SELECT ab.*, c.name AS categoryName
FROM account_book ab
         LEFT JOIN category c ON ab.category_id = c.id
WHERE ab.user_id = #{userId}
  AND ab.date BETWEEN #{startDate} AND #{endDate}
ORDER BY ab.date DESC

mapper.update:
UPDATE account_book
SET amount = #{amount}, type = #{type}, category_id = #{categoryId},
    date = #{date}, note = #{note}
WHERE id = #{id} AND user_id = #{userId}
